@charset "UTF-8";
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Open Sans', sans-serif;
  background-color: #e2e2e2;
}

html {
  font-size: 14px;
}

.container {
  max-width: 1300px;
  margin: 0 auto;
}

a {
  text-decoration: none;
  color: #333;
  outline: none;
}

.main-title {
  background-color: #333;
  text-transform: uppercase;
  text-align: center;
  padding: 1rem 0;
  color: #ffffff;
  position: relative;
  border-bottom: 0.2rem solid #ff6666;
}

.header {
  height: 10rem;
  background-image: url("/src/img/top3.jpg");
  background-position: top center;
}

.header__container {
  display: flex;
  align-items: center;
  height: 100%;
  position: relative;
  justify-content: space-between;
}

.header__auth {
  position: absolute;
  right: 0;
  top: 1.8rem;
}

.header__nav {
  height: 5rem;
  background-color: #333;
  border-top: solid #ff6666;
  border-bottom: solid #ff6666;
  position: relative;
}

.header__logo {
  background: url("/src/img/yummy-slogan.svg") no-repeat;
  height: 5.3rem;
  width: 16rem;
  display: block;
}

.header__search {
  margin-top: 1rem;
}

.auth {
  display: flex;
  text-transform: uppercase;
}

.auth__item {
  font-size: 1rem;
  font-weight: 600;
  padding: 0.8rem 1.3rem;
}

.auth__login {
  background-color: #f5f5f5;
}

.auth__reg {
  background-color: #3cce7b;
}

.header-menu {
  display: flex;
  list-style: none;
  align-items: stretch;
  height: 100%;
}

.header-menu__item {
  display: flex;
  align-items: center;
  position: relative;
}

.header-menu__item:hover {
  background-color: #ff6666;
}

.header-menu__item.more:hover .header-menu__hide-list {
  display: block;
}

.header-menu__item.more::before {
  content: '';
  position: absolute;
  display: block;
  margin: auto;
  height: 0;
  width: 0;
  left: 0;
  right: 0;
  bottom: -1px;
  border-left: 0.5em solid transparent;
  border-right: 0.5em solid transparent;
  border-bottom: 0.5em solid #ff6666;
}

.header-menu__hide-list {
  position: absolute;
  right: 0;
  background: #333;
  top: 100%;
  display: none;
}

.header-menu__link {
  color: #ffffff;
  padding: 1.7rem 1.5rem;
  text-transform: uppercase;
  font-weight: 300;
  position: relative;
}

.header-menu__link.anime::after {
  content: 'аниме';
  position: absolute;
  left: 50%;
  bottom: 1rem;
  transform: translateX(-50%);
  font-size: 0.8em;
  text-transform: none;
}

.hide-list {
  width: 15rem;
  border-bottom: 0.2rem solid #ff6666;
  border-top: 0.2rem solid #ff6666;
}

.hide-list__item {
  list-style: none;
  display: flex;
  border-bottom: 0.1rem solid #555;
}

.hide-list__item:last-child {
  border: none;
}

.hide-list__item:hover {
  background-color: #555;
}

.hide-list__link {
  padding: 0.6rem 1rem;
  color: #ffffff;
  font-weight: 300;
  flex: 1;
}

.header-search {
  width: 100%;
  padding: 0.8rem 0.9rem;
  font-size: 1.2rem;
  border: 1px solid #ccc;
  outline: none;
  font-weight: 300;
  font-style: italic;
  font-family: inherit;
}

.basic__slider {
  height: 17rem;
  width: 100%;
  background-color: #333;
  margin-top: 10.3rem;
}

.basic__container {
  display: flex;
  justify-content: space-between;
  margin-top: 2rem;
}

.basic__title {
  background-color: #333;
  text-transform: uppercase;
  text-align: center;
  padding: 1rem 0;
  color: #ffffff;
  position: relative;
  border-bottom: 0.2rem solid #ff6666;
}

.basic__all {
  position: absolute;
  right: 1rem;
  color: inherit;
  text-transform: none;
  text-decoration: underline;
}

.basic__update {
  flex: 1;
  margin-right: 1.5rem;
}

.basic__news {
  flex: 1;
  background-color: #333;
}

.basic-item {
  display: flex;
  border-bottom: 0.1rem solid #ccc;
  background-color: #f5f5f5;
}

.basic-item:nth-child(2n-1) {
  background-color: #fff;
}

.basic-item__link {
  flex: 1;
  display: flex;
  align-items: center;
  border-left: 0.3rem solid transparent;
  transition: all 0.1s;
}

.basic-item__link:hover {
  border-left: 0.3rem solid #3cce7b;
}

.basic-item__date {
  padding: 1.3rem 0.4rem;
  border-right: 0.1rem solid #ccc;
}

.basic-item__image {
  height: 4rem;
  width: auto;
}

.basic-item__desc {
  margin-left: 0.5rem;
  flex: 1;
}

.basic-item__title {
  font-weight: bold;
}

.basic-item__genres {
  display: inline-flex;
  list-style: none;
}

.anime-title {
  margin-top: 11rem;
}

.content__container {
  display: flex;
  justify-content: space-between;
  margin-top: 2rem;
}

.content__anime {
  flex: 2;
  margin-right: 1rem;
}

.content__filter {
  flex: 0.6;
}

.new-anime__main-title {
  text-align: left;
}

.new-anime__container {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
}

.new-anime__label {
  background-color: #ff6666;
  padding-top: inherit;
  padding-bottom: inherit;
  padding-left: 2rem;
  padding-right: 2rem;
  color: #ffffff;
}

.new-anime__item {
  display: flex;
  margin-bottom: 1rem;
  background: #ffffff;
  width: 49%;
}

.new-anime__info {
  list-style: none;
}

.new-anime__info > li {
  margin-bottom: 1rem;
}

.new-anime__desc {
  width: inherit;
  padding: 0.6rem;
  flex: 1;
}

.new-anime__img {
  width: 12rem;
  height: 100%;
}

.new-anime__title {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  display: block;
  font-weight: 600;
  font-size: 1.1em;
  text-transform: uppercase;
  margin-bottom: 1rem;
  color: #333;
  transition: color 0.4s ease;
}

.new-anime__title:hover {
  color: #ff6666;
}

.new-anime__genres {
  display: inline-flex;
  list-style: none;
  flex-wrap: wrap;
}

.new-anime__genres a {
  color: #ff6666;
}

.new-anime__genres a:hover {
  text-decoration: underline;
}
