function timeConverter(timestamp) {
  let a = new Date(timestamp * 1)
  const months = [
    'Янв',
    'Фев',
    'Мар',
    'Апр',
    'Мая',
    'Июн',
    'Июл',
    'Авг',
    'Сен',
    'Окт',
    'Ноя',
    'Дек',
  ]
  let year = a.getFullYear()
  let month = months[a.getMonth()]
  let date = a.getDate()
  let hour = a.getHours()
  let min = a.getMinutes()
  let sec = a.getSeconds()

  let time = {
    year,
    month,
    date,
    hour,
    min,
    sec,
    get getDate() {
      return `${this.date} ${this.month}`
    },
  }

  return time
}
function createValidObj(data) {
  let obj = {}
  data.forEach((el) => {
    let fild = el.id
    for (let key in data) {
      if (data[key].id == fild) {
        obj[fild] = Object.keys(el)
          .filter((i) => i !== 'id')
          .map((item) => el[item])
          .join('')
      }
    }
  })
  return obj
}
