.header {
  height: 10rem;
  background-image: url("/src/img/top3.jpg");
  background-position: top center;
}

.header__container {
  display: flex;
  align-items: center;
  height: 100%;
  position: relative;
  justify-content: space-between;
}

.header__auth {
  position: absolute;
  right: 0;
  top: 1.8rem;
}

.header__nav {
  height: 5em;
  background-color: #333;
  border-top: solid #ff6666;
  border-bottom: solid #ff6666;
  position: relative;
  transition: opacity 0.3s, top 0.3s;
}

@media screen and (max-width: 1024px) {
  .header__nav {
    font-size: 0.7em;
  }
}

@media screen and (max-width: 740px) {
  .header__nav {
    display: none;
  }
}

.header__nav.fixed-show {
  z-index: 9999;
  position: fixed;
  top: -4.3em;
  width: 100%;
  opacity: 0.9;
  height: 4.3em;
  font-size: 0.8em;
  border-top: none;
  border-bottom: 0.2em solid #ff6666;
}

@media screen and (max-width: 1024px) {
  .header__nav.fixed-show {
    display: none;
  }
}

.header__nav.fixed-show.anim-navbar {
  top: 0;
}

.header__nav.fixed-show.anim-navbar:hover {
  opacity: 1;
}

.header__logo {
  background: url("/src/img/yummy-slogan.svg") no-repeat;
  height: 5.3em;
  width: 16rem;
  display: block;
}

.header__search {
  margin-top: 1rem;
}

.header__filter {
  background-color: #3cce7b;
  cursor: pointer;
  text-align: center;
  font-size: 1.1em;
  text-transform: uppercase;
  padding: 1em;
  color: #fff;
  margin-top: 1em;
  display: none;
}

@media screen and (max-width: 740px) {
  .header__filter {
    display: block;
  }
}

.auth {
  display: flex;
  text-transform: uppercase;
}

.auth__item {
  font-size: 1rem;
  font-weight: 600;
  padding: 0.8rem 1.3rem;
}

.auth__login {
  background-color: #f5f5f5;
}

.auth__reg {
  background-color: #3cce7b;
}

.header-menu {
  display: flex;
  list-style: none;
  align-items: stretch;
  height: 100%;
}

.header-menu__item {
  display: flex;
  align-items: center;
  position: relative;
}

.header-menu__item:hover {
  background-color: #ff6666;
}

.header-menu__item.more:hover .header-menu__hide-list {
  display: block;
  z-index: 999;
}

.header-menu__item.more::before {
  content: '';
  position: absolute;
  display: block;
  margin: auto;
  height: 0;
  width: 0;
  left: 0;
  right: 0;
  bottom: -1px;
  border-left: 0.5em solid transparent;
  border-right: 0.5em solid transparent;
  border-bottom: 0.5em solid #ff6666;
}

.header-menu__hide-list {
  position: absolute;
  right: 0;
  background: #333;
  top: 100%;
  display: none;
}

.header-menu__link {
  color: #fff;
  padding: 1.7em 1.5em;
  text-transform: uppercase;
  font-weight: 300;
  position: relative;
}

.header-menu__link.anime::after {
  content: 'аниме';
  position: absolute;
  left: 50%;
  bottom: 1em;
  transform: translateX(-50%);
  font-size: 0.8em;
  text-transform: none;
}

.hide-list {
  width: 15rem;
  border-bottom: 0.2rem solid #ff6666;
  border-top: 0.2rem solid #ff6666;
}

.hide-list__item {
  list-style: none;
  display: flex;
  border-bottom: 0.1rem solid #555;
}

.hide-list__item:last-child {
  border: none;
}

.hide-list__item:hover {
  background-color: #555;
}

.hide-list__link {
  padding: 0.6rem 1rem;
  color: #fff;
  font-weight: 300;
  flex: 1;
}

.header-search {
  width: 100%;
  padding: 0.8rem 0.9rem;
  font-size: 1.2rem;
  border: 1px solid #ccc;
  outline: none;
  font-weight: 300;
  font-style: italic;
  font-family: inherit;
}

.basic__slider {
